import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Layout from "./Layout";
import AddExpensePage from "./components/Expenses/AddExpensePage";
import AddIncomePage from "./components/Income/AddIncomePage";
import ExpensesPage from "./components/Expenses/ExpensesPage";
import GoalsPage from "./components/Goals/GoalsPage";
import BadgesPage from "./components/Badges/BadgesPage";
import SettingsPage from "./components/Settings/SettingsPage";
import SignInPage from "./components/Auth/SignInPage";
import SignUpPage from "./components/Auth/SignUpPage";
import BudgetPlannerPage from "./components/Planner/BudgetPlannerPage"; // ✅ Import planner page

function App() {
  const isAuthenticated = !!localStorage.getItem("token");

  return (
    <Router>
      <Routes>
        {/* Public routes */}
        <Route path="/signin" element={<SignInPage />} />
        <Route path="/signup" element={<SignUpPage />} />

        {/* Protected layout routes */}
        {isAuthenticated ? (
          <Route path="/" element={<Layout />}>
            <Route path="add-expense" element={<AddExpensePage />} />
            <Route path="add-income" element={<AddIncomePage />} />
            <Route path="expenses" element={<ExpensesPage />} />
            <Route path="goals" element={<GoalsPage />} />
            <Route path="badges" element={<BadgesPage />} />
            <Route path="planner" element={<BudgetPlannerPage />} /> {/* ✅ Add planner route */}
            <Route path="settings" element={<SettingsPage />} />
            <Route path="*" element={<Navigate to="/add-expense" replace />} />
          </Route>
        ) : (
          <Route path="*" element={<Navigate to="/signin" replace />} />
        )}
      </Routes>
    </Router>
  );
}

export default App;
