import React, { useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { FiBell } from "react-icons/fi";

const Layout = ({ children }) => {
  const location = useLocation();
  const navigate = useNavigate();

  const isActive = (path) => location.pathname === path;

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/signin");
  };

  // Notification state
  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);

  // Fetch notifications from /api/planner/progress
  const fetchNotifications = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setNotifications([]);
      return;
    }
    try {
      const res = await fetch("http://localhost:5000/api/planner/progress", {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        const data = await res.json();
        setNotifications(data.notifications || []);
      } else {
        setNotifications([]);
      }
    } catch (err) {
      console.error("Error fetching notifications", err);
      setNotifications([]);
    }
  };

  useEffect(() => {
    fetchNotifications();

    // Listen for expenseAdded events to refresh notifications
    const onExpenseAdded = () => fetchNotifications();
    window.addEventListener("expenseAdded", onExpenseAdded);

    return () => window.removeEventListener("expenseAdded", onExpenseAdded);
  }, []);

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-60 bg-white shadow-md p-4">
        <h2 className="text-2xl font-bold mb-6">BUDGETLY</h2>
        <nav className="space-y-4">
          {[
            { path: "/dashboard", label: "Dashboard" },
            { path: "/add-expense", label: "Add Expense" },
            { path: "/add-income", label: "Add Income" },
            { path: "/expenses", label: "Expenses" },
            { path: "/goals", label: "Goals" },
            { path: "/badges", label: "Badges" },
            { path: "/planner", label: "Planner" },
          ].map(({ path, label }) => (
            <Link
              key={path}
              to={path}
              className={`block w-full text-left px-4 py-2 rounded-md ${
                isActive(path) ? "bg-teal-500 text-white" : "hover:bg-gray-200"
              }`}
            >
              {label}
            </Link>
          ))}

          <div className="mt-10 border-t pt-4">
            <Link
              to="/settings"
              className="block w-full text-left px-4 py-2 hover:bg-gray-200 rounded-md"
            >
              Settings
            </Link>
            <button
              onClick={handleLogout}
              className="w-full text-left px-4 py-2 hover:bg-gray-200 rounded-md text-red-500"
            >
              Logout
            </button>
          </div>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-8 bg-[#e7f7fe]">
        {/* Top Bar */}
        <div className="mb-6 flex items-center justify-between">
          <input
            type="text"
            placeholder="Search"
            className="w-full max-w-md px-4 py-2 rounded-md border outline-none"
          />

          {/* Notification Bell */}
          <div
            className="relative ml-4 cursor-pointer"
            onClick={() => setShowNotifications(!showNotifications)}
          >
            <FiBell size={24} />
            {notifications.length > 0 && (
              <>
                <span className="absolute top-0 right-0 block h-3 w-3 rounded-full bg-red-600 animate-ping"></span>
                <span className="absolute top-0 right-0 block h-3 w-3 rounded-full bg-red-600"></span>
              </>
            )}

            {/* Notifications dropdown */}
            {showNotifications && (
              <div className="absolute right-0 mt-2 w-72 max-h-64 overflow-y-auto bg-white shadow-lg rounded-md border border-gray-300 z-50">
                <div className="p-4">
                  <h4 className="font-semibold mb-2">Notifications</h4>
                  {notifications.length === 0 ? (
                    <p className="text-gray-500">No notifications</p>
                  ) : (
                    notifications.map((msg, idx) => (
                      <p
                        key={idx}
                        className="text-sm border-b border-gray-200 py-1 last:border-b-0"
                      >
                        {msg}
                      </p>
                    ))
                  )}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Page Content */}
        {children}
      </main>
    </div>
  );
};

export default Layout;
