import React, { useState } from "react";
import { Link, useNavigate, Outlet } from "react-router-dom";
import Notifications from "../components/Notifications";

const Layout = () => {
  const [showNotifications, setShowNotifications] = useState(false);
  const navigate = useNavigate();

  const toggleNotifications = () => {
    setShowNotifications((prev) => !prev);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="min-h-screen flex flex-col">
      <header className="bg-white shadow-md">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-teal-600">Budget Planner</h1>
          <nav className="flex items-center space-x-6">
            <Link to="/planner" className="text-gray-700 hover:text-teal-600">
              Planner
            </Link>
            <Link to="/expenses" className="text-gray-700 hover:text-teal-600">
              Expenses
            </Link>

            {/* Notification Bell */}
            <button
              onClick={toggleNotifications}
              aria-label="Notifications"
              className="relative text-gray-700 hover:text-teal-600 focus:outline-none"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6 6 0 10-12 0v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
                />
              </svg>
            </button>

            <button
              onClick={handleLogout}
              className="text-gray-700 hover:text-teal-600"
            >
              Logout
            </button>
          </nav>
        </div>

        {showNotifications && (
          <div className="absolute top-16 left-64 z-50">
            <Notifications />
          </div>
        )}
      </header>

      <main className="flex-1 container mx-auto px-4 py-6">
        <Outlet />
      </main>
    </div>
  );
};

export default Layout;
