import React, { useEffect, useState } from "react";

const PlannerProgress = () => {
  const [expenses, setExpenses] = useState([]);
  const [limits, setLimits] = useState({}); // Assuming you have limits data too

  // Fetch expenses data
  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://localhost:5000/api/expenses", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (res.ok) {
        setExpenses(data);
      } else {
        console.error("Failed to fetch expenses", data.error);
      }
    } catch (error) {
      console.error("Error fetching expenses", error);
    }
  };

  useEffect(() => {
    fetchExpenses();

    const handleUpdate = () => {
      fetchExpenses();
    };

    const onStorageChange = (e) => {
      if (e.key === "expensesUpdated") {
        fetchExpenses();
      }
    };

    window.addEventListener("expenseAdded", handleUpdate);
    window.addEventListener("storage", onStorageChange);

    return () => {
      window.removeEventListener("expenseAdded", handleUpdate);
      window.removeEventListener("storage", onStorageChange);
    };
  }, []);

  // You probably compute progress per category here, example:
  // const progressByCategory = ... 

  return (
    <div>
      <h2>Planner Progress</h2>
      {/* Render your progress UI here based on expenses and limits */}
      {/* For example: */}
      {Object.entries(limits).map(([category, limit]) => {
        const spent = expenses
          .filter((e) => e.category === category)
          .reduce((sum, e) => sum + e.amount, 0);

        const percentage = Math.min((spent / limit) * 100, 100);

        return (
          <div key={category} style={{ marginBottom: "1rem" }}>
            <h4>{category}</h4>
            <div
              style={{
                width: "100%",
                backgroundColor: "#eee",
                borderRadius: "4px",
                height: "20px",
              }}
            >
              <div
                style={{
                  width: `${percentage}%`,
                  backgroundColor: percentage > 100 ? "red" : "teal",
                  height: "100%",
                  borderRadius: "4px",
                }}
              />
            </div>
            <p>
              Spent: ${spent.toFixed(2)} / ${limit.toFixed(2)} ({percentage.toFixed(1)}%)
            </p>
          </div>
        );
      })}
    </div>
  );
};

export default PlannerProgress;
