import React, { useEffect, useState } from "react";

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);

  const fetchNotifications = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setNotifications([]);
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/planner/progress", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!res.ok) {
        console.error("Failed to fetch planner progress");
        setNotifications([]);
        return;
      }

      const progressData = await res.json();
      const newNotifications = [];

      Object.entries(progressData).forEach(([category, data]) => {
        const pct = data.percentage;

        if (pct >= 100) {
          newNotifications.push({
            id: `100_${category}`,
            message: `🚨 You have spent 100% or more of your planned budget in "${category}"!`,
            level: "danger",
          });
        } else if (pct >= 90) {
          newNotifications.push({
            id: `90_${category}`,
            message: `⚠️ You have spent 90% or more of your planned budget in "${category}".`,
            level: "warning",
          });
        } else if (pct >= 80) {
          newNotifications.push({
            id: `80_${category}`,
            message: `🔔 You have spent 80% or more of your planned budget in "${category}".`,
            level: "info",
          });
        }
      });

      setNotifications(newNotifications);
    } catch (error) {
      console.error("Error fetching notifications", error);
      setNotifications([]);
    }
  };

  useEffect(() => {
    fetchNotifications();

    const onExpenseAdded = () => {
      fetchNotifications();
    };

    window.addEventListener("expenseAdded", onExpenseAdded);

    return () => {
      window.removeEventListener("expenseAdded", onExpenseAdded);
    };
  }, []);

  return (
    <div className="relative">
      <div className="absolute right-0 mt-2 w-72 max-h-64 overflow-auto bg-white shadow-lg rounded border border-gray-300 z-10 p-4">
        {notifications.length === 0 ? (
          <p className="text-gray-500 text-center">No spending alerts yet.</p>
        ) : (
          <ul className="space-y-2">
            {notifications.map(({ id, message, level }) => {
              let bgColor = "bg-blue-100 text-blue-800";
              if (level === "danger") bgColor = "bg-red-100 text-red-800";
              else if (level === "warning") bgColor = "bg-yellow-100 text-yellow-800";

              return (
                <li
                  key={id}
                  className={`${bgColor} p-3 rounded-md shadow-sm font-semibold`}
                >
                  {message}
                </li>
              );
            })}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Notifications;
