import React, { useState } from 'react';
import { createPlanner, getPlanner } from '../../api/planner';



function BudgetPlannerPage() {
  const [initialBudget, setInitialBudget] = useState('');
  const [spendingLimits, setSpendingLimits] = useState({});
  const [error, setError] = useState(null);

  // Assume you have some way to get the user's token from localStorage or context
  const token = localStorage.getItem('token'); // or however you store the JWT

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Log token for debugging
    console.log('Sending token:', token);

    try {
      const plannerData = {
        initialBudget,
        spendingLimits,
      };

      const response = await createPlanner(plannerData, token);
      console.log('Plan created successfully:', response);
      setError(null);
      // Handle successful creation (e.g. navigate or show success message)
    } catch (err) {
      console.error('Error creating planner:', err.response?.data || err.message);
      setError(err.response?.data?.error || 'Failed to create plan');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Your input fields */}
      <input
        type="number"
        placeholder="Initial Budget"
        value={initialBudget}
        onChange={(e) => setInitialBudget(e.target.value)}
        required
      />
      {/* More inputs for spendingLimits here */}

      <button type="submit">Create Plan</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </form>
  );
}

export default BudgetPlannerPage;
