import React, { useState } from "react";

const Planner = () => {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [notes, setNotes] = useState("");

  const addTask = () => {
    if (!title.trim()) return;
    const newTask = {
      id: Date.now(),
      title: title.trim(),
      notes: notes.trim(),
      completed: false,
    };
    setTasks([newTask, ...tasks]);
    setTitle("");
    setNotes("");
  };

  const toggleTask = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div className="max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Planner</h1>

      {/* Task Input */}
      <div className="bg-white p-4 rounded-md shadow mb-6">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full mb-2 px-4 py-2 border rounded-md outline-none"
        />
        <textarea
          placeholder="Notes (optional)"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          className="w-full mb-2 px-4 py-2 border rounded-md outline-none"
          rows={3}
        />
        <button
          onClick={addTask}
          className="bg-teal-500 text-white px-4 py-2 rounded hover:bg-teal-600"
        >
          Add Task
        </button>
      </div>

      {/* Task List */}
      <div className="space-y-4">
        {tasks.length === 0 ? (
          <p className="text-gray-600">No tasks added yet.</p>
        ) : (
          tasks.map((task) => (
            <div
              key={task.id}
              className={`p-4 rounded-md shadow bg-white flex justify-between items-start ${
                task.completed ? "opacity-50 line-through" : ""
              }`}
            >
              <div>
                <h2 className="text-lg font-semibold">{task.title}</h2>
                {task.notes && (
                  <p className="text-sm text-gray-600 mt-1">{task.notes}</p>
                )}
              </div>
              <div className="space-x-2">
                <button
                  onClick={() => toggleTask(task.id)}
                  className="px-3 py-1 rounded bg-green-100 hover:bg-green-200 text-green-800 text-sm"
                >
                  {task.completed ? "Undo" : "Done"}
                </button>
                <button
                  onClick={() => deleteTask(task.id)}
                  className="px-3 py-1 rounded bg-red-100 hover:bg-red-200 text-red-800 text-sm"
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Planner;
