import React, { useEffect, useState } from "react";

const PlannerProgress = ({ refresh }) => {
  const [progressData, setProgressData] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchProgress = async () => {
    setLoading(true);
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const res = await fetch("http://localhost:5000/api/planner/progress", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (res.ok) {
        const data = await res.json();
        setProgressData(data.categories);
      } else {
        setProgressData(null);
      }
    } catch (err) {
      setProgressData(null);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProgress();
  }, [refresh]);

  if (loading) return <p>Loading progress...</p>;
  if (!progressData) return <p>No active planner found.</p>;

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Current Spending Progress</h2>
      <ul>
        {Object.entries(progressData).map(([category, { limit, spent }]) => {
          const percent = limit > 0 ? Math.min((spent / limit) * 100, 100) : 0;
          return (
            <li key={category} className="mb-3">
              <strong>{category}</strong>: ${spent.toFixed(2)} / ${limit.toFixed(2)}{" "}
              ({percent.toFixed(0)}%)
              <div className="w-full bg-gray-200 rounded h-4 mt-1">
                <div
                  className="bg-teal-500 h-4 rounded"
                  style={{ width: `${percent}%` }}
                ></div>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default PlannerProgress;
