import React, { useEffect, useState } from "react";

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [visible, setVisible] = useState(false);

  const fetchNotifications = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setNotifications([]);
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/planner/notifications", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (res.ok) {
        const data = await res.json();
        // Expecting data.notifications as array with {id, message, level}
        setNotifications(data.notifications || []);
      } else {
        setNotifications([]);
      }
    } catch (error) {
      console.error("Error fetching notifications", error);
      setNotifications([]);
    }
  };

  useEffect(() => {
    fetchNotifications();

    const onExpenseAdded = () => {
      fetchNotifications();
    };

    window.addEventListener("expenseAdded", onExpenseAdded);

    return () => {
      window.removeEventListener("expenseAdded", onExpenseAdded);
    };
  }, []);

  return (
    <div className="relative">
      <button
        onClick={() => setVisible(!visible)}
        className="relative focus:outline-none"
        aria-label="Notifications"
      >
        {/* Bell icon */}
        <svg
          className="w-6 h-6 text-gray-600"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6 6 0 10-12 0v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
          ></path>
        </svg>
        {notifications.length > 0 && (
          <span className="absolute top-0 right-0 inline-block w-2 h-2 bg-red-600 rounded-full"></span>
        )}
      </button>

      {visible && (
        <div className="absolute right-0 mt-2 w-72 max-h-64 overflow-auto bg-white shadow-lg rounded border border-gray-300 z-10 p-4">
          {notifications.length === 0 ? (
            <p className="text-gray-500 text-center">No notifications.</p>
          ) : (
            <ul className="space-y-2">
              {notifications.map(({ id, message, level }) => {
                let bgColor = "bg-blue-100 text-blue-800";
                if (level === "danger") bgColor = "bg-red-100 text-red-800";
                else if (level === "warning") bgColor = "bg-yellow-100 text-yellow-800";

                return (
                  <li
                    key={id}
                    className={`${bgColor} p-3 rounded-md shadow-sm font-semibold`}
                  >
                    {message}
                  </li>
                );
              })}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default Notifications;
