import React, { useEffect, useState } from "react";

const PlannerProgress = ({ refresh }) => {
  const [progressData, setProgressData] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchProgress = async () => {
    setLoading(true);
    const token = localStorage.getItem("token");
    if (!token) {
      setProgressData(null);
      setLoading(false);
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/planner/progress", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (res.ok) {
        const data = await res.json();
        setProgressData(data.categories);
      } else {
        setProgressData(null);
      }
    } catch (error) {
      console.error("Error fetching progress", error);
      setProgressData(null);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProgress();

    const onExpenseAdded = () => {
      fetchProgress();
    };

    window.addEventListener("expenseAdded", onExpenseAdded);

    return () => {
      window.removeEventListener("expenseAdded", onExpenseAdded);
    };
  }, [refresh]);  // <--- listen for refresh prop changes here

  if (loading) return <p>Loading progress...</p>;
  if (!progressData) return <p>No progress data available.</p>;

  return (
    <div>
      {Object.entries(progressData).map(([category, { limit, spent }]) => (
        <div key={category}>
          <h3>{category}</h3>
          <progress max={limit} value={spent}></progress>
          <p>{spent} / {limit}</p>
        </div>
      ))}
    </div>
  );
};

export default PlannerProgress;
