import React, { useState, useEffect } from "react";

const Planner = () => {
  const categoryList = [
    "Groceries",
    "Bills, rent, insurance",
    "Entertainment & Lifestyle",
    "Unexpected",
    "Medical",
  ];

  const [initialAmount, setInitialAmount] = useState("");
  const [planPeriod, setPlanPeriod] = useState("");
  const [spendingLimits, setSpendingLimits] = useState(
    categoryList.reduce((acc, cat) => {
      acc[cat] = "";
      return acc;
    }, {})
  );

  // Load saved plan if it exists
  useEffect(() => {
    const savedPlanner = JSON.parse(localStorage.getItem("plannerData"));
    if (savedPlanner) {
      setInitialAmount(savedPlanner.initialAmount || "");
      setPlanPeriod(savedPlanner.planPeriod || "");
      setSpendingLimits(savedPlanner.spendingLimits || {});
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    const plannerData = {
      initialAmount,
      planPeriod,
      spendingLimits,
    };

    // Temporary: store in localStorage until backend is added
    localStorage.setItem("plannerData", JSON.stringify(plannerData));
    alert("Planner data saved!");
  };

  return (
    <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow">
      <h1 className="text-2xl font-bold mb-6">Planner Setup</h1>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block font-medium mb-1">Initial Amount (your starting balance)</label>
          <input
            type="number"
            value={initialAmount}
            onChange={(e) => setInitialAmount(e.target.value)}
            className="w-full border rounded px-4 py-2"
            required
          />
        </div>

        <div>
          <label className="block font-medium mb-1">Plan Period (e.g. June 2025 or Q3)</label>
          <input
            type="text"
            value={planPeriod}
            onChange={(e) => setPlanPeriod(e.target.value)}
            className="w-full border rounded px-4 py-2"
            required
          />
        </div>

        <div>
          <h2 className="text-lg font-semibold mb-2">Set Spending Limits by Category</h2>
          {categoryList.map((category) => (
            <div key={category} className="flex items-center gap-4 mb-2">
              <label className="w-56">{category}</label>
              <input
                type="number"
                min="0"
                value={spendingLimits[category]}
                onChange={(e) =>
                  setSpendingLimits({
                    ...spendingLimits,
                    [category]: e.target.value,
                  })
                }
                className="border rounded px-3 py-1 w-48"
              />
            </div>
          ))}
        </div>

        <button
          type="submit"
          className="bg-teal-500 text-white px-6 py-2 rounded hover:bg-teal-600"
        >
          Save Plan
        </button>
      </form>
    </div>
  );
};

export default Planner;
