import React, { useEffect, useState } from "react";

const PlannerProgress = ({ refresh }) => {
  const [progressData, setProgressData] = useState(null);
  const [error, setError] = useState(null);

  const fetchProgress = async () => {
    console.log("📡 Fetching planner progress...");
    const token = localStorage.getItem("token");
    if (!token) {
      setError("No auth token found. Please login.");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/planner/progress", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.ok) {
        const data = await res.json();
        console.log("✅ Planner progress data received:", data);
        setProgressData(data.categories);
        setError(null);
      } else {
        const errText = await res.text();
        setError("Failed to load planner progress: " + errText);
      }
    } catch (err) {
      console.error("❌ Error fetching progress:", err);
      setError("Error connecting to backend.");
    }
  };

  useEffect(() => {
    fetchProgress();

    const handleExpenseAdded = (e) => {
      const { category, amount } = e.detail || {};
      console.log("🎯 expenseAdded event caught:", e.detail);

      if (!category || !amount) {
        console.warn("⚠️ Missing detail in expenseAdded event. Fallback to fetch.");
        fetchProgress();
        return;
      }

      // Safely update progressData with new expense
      setProgressData((prev) => {
        if (!prev) return prev;

        const updated = JSON.parse(JSON.stringify(prev)); // deep copy

        if (updated[category]) {
          updated[category].spent += amount;
        } else {
          updated[category] = { limit: 0, spent: amount };
        }

        console.log("🔄 Local progressData updated:", updated);
        return updated;
      });

      // Also re-fetch from backend to ensure full sync
      fetchProgress();
    };

    window.addEventListener("expenseAdded", handleExpenseAdded);
    return () => {
      window.removeEventListener("expenseAdded", handleExpenseAdded);
    };
  }, []);

  // NEW useEffect to refresh progress when parent toggles `refresh` prop
  useEffect(() => {
    if (refresh !== undefined) {
      fetchProgress();
    }
  }, [refresh]);

  useEffect(() => {
    console.log("📊 Updated progressData:", progressData);
  }, [progressData]);

  if (error) {
    return <div className="text-red-600 font-semibold">{error}</div>;
  }

  if (!progressData) {
    return <div>Loading planner progress...</div>;
  }

  return (
    <div className="max-w-lg mx-auto p-4 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">Planner Spending Progress</h2>
      {Object.entries(progressData).map(([category, { limit, spent }]) => {
        const percent = limit > 0 ? Math.min((spent / limit) * 100, 100) : 0;
        const overSpent = spent > limit;

        return (
          <div key={category} className="mb-4">
            <div className="flex justify-between mb-1 font-medium">
              <span>{category}</span>
              <span>
                ${spent.toFixed(2)} / ${limit.toFixed(2)}
              </span>
            </div>
            <div className="w-full h-6 bg-gray-300 rounded-full overflow-hidden">
              <div
                className={`h-full ${
                  overSpent ? "bg-red-500" : "bg-teal-500"
                } transition-all duration-500`}
                style={{ width: `${percent}%` }}
              />
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default PlannerProgress;
