import React, { useEffect, useState } from "react";

const PlannerProgress = ({ refresh }) => {
  const [categories, setCategories] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchProgress = async () => {
      if (!token) {
        setCategories(null);
        setLoading(false);
        return;
      }

      setLoading(true);
      try {
        const res = await fetch("http://localhost:5000/api/planner/progress", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (res.ok) {
          const data = await res.json();
          setCategories(data.categories);
        } else if (res.status === 404) {
          setCategories(null);
        } else {
          alert("Failed to fetch progress");
        }
      } catch {
        alert("Error connecting to backend");
      } finally {
        setLoading(false);
      }
    };

    fetchProgress();
  }, [refresh, token]);

  if (loading) {
    return <div>Loading progress...</div>;
  }

  if (!categories) {
    return <div>No active planner found.</div>;
  }

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Spending Progress</h2>
      <div className="space-y-4">
        {Object.entries(categories).map(([category, { limit, spent }]) => {
          const percentSpent = limit > 0 ? Math.min((spent / limit) * 100, 100) : 0;
          return (
            <div key={category}>
              <div className="flex justify-between mb-1">
                <span>{category}</span>
                <span>
                  {spent.toFixed(2)} / {limit.toFixed(2)}
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded h-5">
                <div
                  className={`h-5 rounded ${
                    percentSpent > 100
                      ? "bg-red-600"
                      : percentSpent > 80
                      ? "bg-yellow-400"
                      : "bg-green-500"
                  }`}
                  style={{ width: `${percentSpent}%` }}
                />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default PlannerProgress;
