import React, { useState } from "react";
import { createPlanner } from "../../api/planner"; // ✅ adjust path as needed
import jwtDecode from "jwt-decode"; // fixed import

const categories = [
  "Groceries",
  "Bills, rent, insurance",
  "Entertainment & Lifestyle",
  "Unexpected",
  "Medical",
];

const BudgetPlannerPage = () => {
  const [initialBudget, setInitialBudget] = useState("");
  const [spendingLimits, setSpendingLimits] = useState(
    categories.reduce((acc, cat) => ({ ...acc, [cat]: "" }), {})
  );
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleLimitChange = (category, value) => {
    setSpendingLimits((prev) => ({
      ...prev,
      [category]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const totalLimit = Object.values(spendingLimits).reduce(
      (sum, val) => sum + Number(val || 0),
      0
    );

    if (totalLimit > Number(initialBudget)) {
      setError("Total spending limits exceed your initial budget.");
      setSuccess("");
      return;
    }

    const token = localStorage.getItem("token");
    if (!token) {
      setError("You must be signed in to save your plan.");
      setSuccess("");
      return;
    }

    try {
      const decoded = jwtDecode(token);
      const userId = decoded.sub; // Use 'sub' from JWT payload as userId

      const today = new Date();
      const startDate = today.toISOString(); // current date ISO string
      const endDate = new Date(today.getFullYear(), today.getMonth() + 1, today.getDate()).toISOString(); // one month later

      const plannerData = {
        userId,
        startAmount: initialBudget,
        startDate,
        endDate,
        categories: spendingLimits,
      };

      await createPlanner(plannerData, token);
      setError("");
      setSuccess("Plan saved successfully!");
    } catch (err) {
      console.error(err);
      setError("Failed to save your plan.");
      setSuccess("");
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow">
      <h2 className="text-2xl font-semibold mb-4">Budget Planner</h2>

      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block font-medium mb-1">Initial Budget (Total Money You Have)</label>
          <input
            type="number"
            className="w-full border px-3 py-2 rounded"
            value={initialBudget}
            onChange={(e) => setInitialBudget(e.target.value)}
            required
            min="0"
          />
        </div>

        <h3 className="font-semibold mt-6 mb-2">Spending Limits by Category</h3>
        {categories.map((category) => (
          <div key={category} className="mb-3">
            <label className="block mb-1">{category}</label>
            <input
              type="number"
              className="w-full border px-3 py-2 rounded"
              value={spendingLimits[category]}
              onChange={(e) => handleLimitChange(category, e.target.value)}
              min="0"
              required
            />
          </div>
        ))}

        {error && <p className="text-red-500 mt-2">{error}</p>}
        {success && <p className="text-green-500 mt-2">{success}</p>}

        <button
          type="submit"
          className="mt-4 px-6 py-2 bg-teal-600 text-white rounded hover:bg-teal-700"
        >
          Save Plan
        </button>
      </form>
    </div>
  );
};

export default BudgetPlannerPage;
